/* eslint-disable max-len */
/* eslint-disable require-jsdoc */
function addToolbox () {

    const iconURI = 'data:image/svg+xml;base64,';

    return `
<category name="%{BKY_TJABKITEXT_CATEGORY}" id="TJABKITEXT_CATEGORY" colour="#4C97FF" secondaryColour="#3373CC"  iconURI="${iconURI}">
        <block type="tjabKitExt_playSound" id="tjabKitExt_playSound">
        <value name="FREQ">
            <shadow type="math_whole_number">
                <field name="NUM">262</field>
            </shadow>
        </value>
        <value name="TIME">
            <shadow type="math_positive_number">
                <field name="NUM">1</field>
            </shadow>
        </value>
    </block>
    <block type="tjabKitExt_playToneForBeat" id="tjabKitExt_playToneForBeat">
        <field name="FREQ">note_C4</field>
    </block>
    <block type="tjabKitExt_setTempo" id="tjabKitExt_setTempo">
        <value name="BPM">
            <shadow type="math_whole_number">
                <field name="NUM">120</field>
            </shadow>
        </value>
    </block>
    <block type="tjabKitExt_playRingtone" id="tjabKitExt_playRingtone">
    </block>
    <sep gap="36"/>
    <block type="tjabKitExt_onBoardDriverRun" id="tjabKitExt_onBoardDriverRun">
        <value name="SPEED">
            <shadow type="math_int9_number">
                <field name="NUM">255</field>
            </shadow>
        </value>
    </block>
    <block type="tjabKitExt_onBoardDriverStop" id="tjabKitExt_onBoardDriverStop">
    </block>
    <block type="tjabKitExt_onBoardDriverLock" id="tjabKitExt_onBoardDriverLock">
    </block>       
    <block type="tjabKitExt_CarRun" id="tjabKitExt_CarRun">
        <value name="SPEED">
            <shadow type="math_int9_number">
                <field name="NUM">255</field>
            </shadow>
        </value>
        <value name="SPEED_ERRO">
            <shadow type="math_int9_number">
                <field name="NUM">0</field>
            </shadow>
        </value>
    </block>   
</category>
`;
}

exports = addToolbox;
